   		if(_resultData.status){
   			successCallback(_resultData);
   		}else{
   			failCallback(_resultData);
   		}
   };

   if(isAndroid){

   }else{

   }
},
receive:function(respond){
	this.callBackCache[respond.callbackId](respond);
}
```

